if (pm.request.method !== 'GET') {
    if (pm.environment.get('XSRF-TOKEN')) {
        pm.request.headers.upsert({
            key: 'x-xsrf-token',
            value: pm.environment.get('XSRF-TOKEN'),
        })
    } else { 
        pm.sendRequest(pm.environment.get('API_URL') + '/sanctum/csrf-cookie', function(error, response) {
            pm.expect(error).to.equal(null)
            pm.expect(response).to.have.property('code', 204)
            pm.expect(response).to.have.property('status', 'No Content')

            let xsrfCookie = response.headers.find(header => header.key.toLowerCase() === 'set-cookie' && header.value.includes('XSRF-TOKEN'))

            if (xsrfCookie) {
                let xsrfToken = decodeURIComponent(xsrfCookie.value.split(';')[0].split('=')[1])

                pm.request.headers.upsert({
                    key: 'x-xsrf-token',
                    value: xsrfToken,
                })

                pm.environment.set('XSRF-TOKEN', xsrfToken)
            }
        })
    }
}

public function store(Request $request)
{
  $data = $request->validate([
    'title' => ['required', 'string'],
    'content' => ['required', 'string'],
    'cover' => ['nullable', 'image'],
  ]);

  $post = new Post();
  $post->title = $data['title'];
  $post->content = $data['content'];

  if ($request->hasFile('cover')) {
    $post->cover = $request->file('cover')->store('', 'posts');
  }

  $post->save();

  return redirect()->back()->withSuccess(__('posts.created'));
}

