use Illuminate\Foundation\Configuration\Middleware;

Application::configure()
  ->withMiddleware(function (Middleware $middleware) {
      $middleware->redirectTo(
          guests: fn () => route('login'),
          users: fn () => route('dashboard'),
      );

      $middleware->encryptCookies(except: [
          'name',
      ]);

      $middleware->preventRequestsDuringMaintenance(except: [
          '/horizon*',
      ]);

      $middleware->statefulApi(); // для Sanctum SPA
      $middleware->throttleApi(); // throttle по умолчанию выключен

      /* Вместо старого свойства $middleware */
      $middleware->append([
         GlobalMiddleware::class,
      ]);

      /* Вместо старого свойства $middlewareGroups */
      $middleware->web(append: [
          WebMiddleware::class,
      ]);

      $middleware->api(remove: [
          SubstituteBindings::class,
      ]);

      /* Вместо старого свойства $middlewareAliases */
      $middleware->alias([
          'custom-alias' => CustomMiddleware::class,
      ]);
  })

